const options={radius:150,variation:10,points:5},close=document.getElementsByClassName("close");for(var i=0;i<close.length;i++)close[i].addEventListener("click",t=>explode(t,options));function explode(t,e){const n=document.createElement("div"),a=t.target;n.classList.add("particles-container"),a.appendChild(n);for(let t=0;t<e.points;t++){const s=360/e.points*(t+1),o=s+parseFloat(e.variation),i=randomAngleBetween(s-parseFloat(e.variation),o),d=Math.cos(i)*e.radius,l=Math.sin(i)*e.radius,r=document.createElement("div");r.classList.add("particle"),r.style.top=l+"px",r.style.left=d+"px",n.appendChild(r),0==t&&r.addEventListener("animationend",()=>{$(a).parent().hasClass("music")&&$(a).parent().addClass("spin-out").addEventListener("animationend",()=>{$(this).removeClass("spin-out")}),$(a).parent().parent().hasClass("popup")&&$(a).parent().parent().addClass("spin-out").addEventListener("animationend",()=>{$(this).removeClass("spin-out")}),a.removeChild(n)})}}function randomAngleBetween(t,e){return(Math.random()*(e-t)+t)/180*Math.PI-Math.PI/2}function handleInput(){const t=this.dataset.option;options[t]=this.value,"duration"==t&&document.documentElement.style.setProperty("--duration",`${this.value}s`);const e=document.querySelector(`.${t}-value`);e&&(e.textContent=this.value)}